#include "ScalarConverter.hpp"

#define BASIC_TESTS "------------------ BASIC TESTS ------------------"
#define FLOAT_TESTS "------------------ FLOAT TESTS ------------------"
#define INF_TESTS "------------------ INF TESTS ------------------"
#define RANGE_TESTS "------------------ RANGE TESTS ------------------"
#define CHAR_TESTS "------------------ CHAR TESTS ------------------"

void	runTest(std::string test)
{
	std::cout << "Testing: \"" << test << "\"" << std::endl;
	ScalarConverter::convert(test);
	std::cout << std::endl;
}

int	main(int argc, char **argv)
{
	if (argc == 2)
	{
		ScalarConverter::convert(argv[1]);
		return 0;
	}

	std::cout << std::endl << std::endl << BASIC_TESTS << std::endl;
	runTest("42");
	runTest("-42");
	runTest("-");
	runTest(".");
	runTest(",");
	runTest("");
	runTest("0");
	runTest("-0");
	runTest("0000000");
	runTest("420");
	runTest("483a649");
	runTest("ÃŸ");
	runTest("apple");
	runTest(" ");
	runTest("                         ");
	runTest("\n");
	runTest("\t");
	runTest("\v");
	runTest("\r");

	std::cout << std::endl << std::endl << FLOAT_TESTS << std::endl;
	runTest("42.0f ");
	runTest(" 42.0f");
	runTest(" 42.0f ");
	runTest(" 43.0f	");
	runTest(" 44.0f\v");
	runTest("42.0f");
	runTest("42.f");
	runTest(".3f");
	runTest(".f");
	runTest("123.123f");
	runTest("123.123f.");
	runTest("123123f.");
	runTest("123123.f");
	runTest("123.123f.23");
	runTest("123.123.23");
	runTest("123.123.0");
	runTest("123..23");
	runTest("123.123.1f");
	runTest("123.1f1");
	runTest("f");
	runTest("F");
	runTest("35.3F");
	runTest("29.3");
	runTest("29. 3");

	std::cout << std::endl << std::endl << INF_TESTS << std::endl;
	runTest("nan");
	runTest("nanf");
	runTest("inf");
	runTest("inff");
	runTest("+inff");
	runTest("+inf");
	runTest("-inf");
	runTest("-inff");

	std::cout << std::endl << std::endl << RANGE_TESTS << std::endl;
	runTest("127");
	runTest("128");
	runTest("-128");
	runTest("-129");
	runTest("2147483647");
	runTest("2147483648");
	runTest("-2147483648");
	runTest("-2147483649");
	runTest("340282346638528859811704183484516925440.0f");
	runTest("340282346638528859811704183484516925441.0f");
	runTest("-340282346638528859811704183484516925440.0f");
	runTest("-340282346638528859811704183484516925441.0f");
	runTest(".0");
	runTest(".0");
	runTest("-.0");
	runTest("-.0");

	std::cout << std::endl << std::endl << CHAR_TESTS << std::endl;
	for (int i = -1; i < 129; i++)
	{
		runTest(std::to_string(i));
	}
}
